sys_script_email.script:
(function runMailScript(current, template, email, email_action, event) {
	template.print('<p><font size="3" color="#808080" face="helvetica">');
	//template.print('Number: ' + current.number + '</br>');
	template.print('\n');
	template.print('Category: ' + current.category.getDisplayValue() + '<br/>');
template.print('\n');
	template.print('Short Description: ' + current.short_description + '<br/>');
	template.print('\n');
	template.print('Description: ' + current.description + '<br/>');
	template.print('\n');
	template.print('Acceptance Criteria: ' + current.acceptance_criteria + '<br/>' + '<br/>');
	template.print('\n');
	template.print('Estimated Hours: ' + current.estimated_hours.getDisplayValue() + '<br/>');
	template.print('</font></p>');

})(current, template, email, email_action, event);
